export default function WordEditor() {

    wrapper.innerHTML = ''
    const wrapperRef = useCallback((wrapper) => {
        wrapper.append(editor)
        new Quill(editor, {theme: "snow"})

        return () => {
            wrapper.innerHTML = ""
        }
    }, [])


    return <div id="container" ref={wrapperRef}></div>
}
